INSERT [dbo].[Story] ([story_id], [story_title], [author_id], [story_price], [story_sale], [story_image], [story_description], [story_description_markdown], [story_description_html], [create_time], [update_time], [status]) VALUES (4, N'The Girl on the Train', 4, CAST(140.00 AS Decimal(10, 2)), CAST(0 AS Decimal(18, 0)), N'22557272202404201726499892.jpg', N'Rachel catches the same commuter train every morning. She knows it will wait at the same signal each time, overlooking a row of back gardens. She’s even started to feel like she knows the people who live in one of the houses. “Jess and Jason,” she calls them. Their life—as she sees it—is perfect. If only Rachel could be that happy. And then she sees something shocking. It’s only a minute until the train moves on, but it’s enough. Now everything’s changed. Now Rachel has a chance to become a part of the lives she’s only watched from afar. Now they’ll see; she’s much more than just the girl on the train...', N'Rachel catches the same commuter train every morning. She knows it will wait at the same signal each time, overlooking a row of back gardens. She’s even started to feel like she knows the people who live in one of the houses. “Jess and Jason,” she calls them. Their life—as she sees it—is perfect. If only Rachel could be that happy. And then she sees something shocking. It’s only a minute until the train moves on, but it’s enough. Now everything’s changed. Now Rachel has a chance to become a part of the lives she’s only watched from afar. Now they’ll see; she’s much more than just the girl on the train...', N'Rachel catches the same commuter train every morning. She knows it will wait at the same signal each time, overlooking a row of back gardens. She’s even started to feel like she knows the people who live in one of the houses. “Jess and Jason,” she calls them. Their life—as she sees it—is perfect. If only Rachel could be that happy. And then she sees something shocking. It’s only a minute until the train moves on, but it’s enough. Now everything’s changed. Now Rachel has a chance to become a part of the lives she’s only watched from afar. Now they’ll see; she’s much more than just the girl on the train...', CAST(N'2022-04-01T05:52:10.323' AS DateTime), NULL, 1)
INSERT [dbo].[Story] ([story_id], [story_title], [author_id], [story_price], [story_sale], [story_image], [story_description], [story_description_markdown], [story_description_html], [create_time], [update_time], [status]) VALUES (5, N'Lord of the Mysteries', 5, CAST(120.00 AS Decimal(10, 2)), CAST(10 AS Decimal(18, 0)), N'Lord-of-the-Mysteries202404201727416057.jpeg', N'Waking up to be faced with a string of mysteries, Zhou Mingrui finds himself reincarnated as Klein Moretti in an alternate Victorian era world where he sees a world filled with machinery, cannons, dreadnoughts, airships, difference machines, as well as Potions, Divination, Hexes, Tarot Cards, Sealed Artifacts… The Light continues to shine but the mystery has never gone far. Follow Klein as he finds himself entangled with the Churches of the world—both orthodox and unorthodox—while he slowly develops newfound powers thanks to the Beyonder potions.', N'Waking up to be faced with a string of mysteries, Zhou Mingrui finds himself reincarnated as Klein Moretti in an alternate Victorian era world where he sees a world filled with machinery, cannons, dreadnoughts, airships, difference machines, as well as Potions, Divination, Hexes, Tarot Cards, Sealed Artifacts… The Light continues to shine but the mystery has never gone far. Follow Klein as he finds himself entangled with the Churches of the world—both orthodox and unorthodox—while he slowly develops newfound powers thanks to the Beyonder potions.', N'Waking up to be faced with a string of mysteries, Zhou Mingrui finds himself reincarnated as Klein Moretti in an alternate Victorian era world where he sees a world filled with machinery, cannons, dreadnoughts, airships, difference machines, as well as Potions, Divination, Hexes, Tarot Cards, Sealed Artifacts… The Light continues to shine but the mystery has never gone far. Follow Klein as he finds himself entangled with the Churches of the world—both orthodox and unorthodox—while he slowly develops newfound powers thanks to the Beyonder potions.', CAST(N'2022-05-01T05:52:10.323' AS DateTime), NULL, 1)
INSERT [dbo].[Story] ([story_id], [story_title], [author_id], [story_price], [story_sale], [story_image], [story_description], [story_description_markdown], [story_description_html], [create_time], [update_time], [status]) VALUES (6, N'The Shining', 6, CAST(140.00 AS Decimal(10, 2)), CAST(0 AS Decimal(18, 0)), N'11588202404201728216297.jpg', N'Jack Torrance''s new job at the Overlook Hotel is the perfect chance for a fresh start. As the off-season caretaker at the atmospheric old hotel, he''ll have plenty of time to spend reconnecting with his family and working on his writing. But as the harsh winter weather sets in, the idyllic location feels ever more remote...and more sinister. And the only one to notice the strange and terrible forces gathering around the Overlook is Danny Torrance, a uniquely gifted five-year-old.', N'Jack Torrance''s new job at the Overlook Hotel is the perfect chance for a fresh start. As the off-season caretaker at the atmospheric old hotel, he''ll have plenty of time to spend reconnecting with his family and working on his writing. But as the harsh winter weather sets in, the idyllic location feels ever more remote...and more sinister. And the only one to notice the strange and terrible forces gathering around the Overlook is Danny Torrance, a uniquely gifted five-year-old.', N'Jack Torrance''s new job at the Overlook Hotel is the perfect chance for a fresh start. As the off-season caretaker at the atmospheric old hotel, he''ll have plenty of time to spend reconnecting with his family and working on his writing. But as the harsh winter weather sets in, the idyllic location feels ever more remote...and more sinister. And the only one to notice the strange and terrible forces gathering around the Overlook is Danny Torrance, a uniquely gifted five-year-old.', CAST(N'2022-06-01T05:52:10.323' AS DateTime), NULL, 1)
INSERT [dbo].[Story] ([story_id], [story_title], [author_id], [story_price], [story_sale], [story_image], [story_description], [story_description_markdown], [story_description_html], [create_time], [update_time], [status]) VALUES (7, N'It', 6, CAST(112.00 AS Decimal(10, 2)), CAST(0 AS Decimal(18, 0)), N'830502202404201729067380.jpg', N'Welcome to Derry, Maine ...
		It’s a small city, a place as hauntingly familiar as your own hometown. Only in Derry the haunting is real ...
		They were seven teenagers when they first stumbled upon the horror. Now they are grown-up men and women who have gone out into the big world to gain success and happiness. But none of them can withstand the force that has drawn them back to Derry to face the nightmare without an end, and the evil without a name.', N'Welcome to Derry, Maine ...
